void handleON() {
  irsend.sendRaw(rawDataON, 279, 38);  // Send a raw data capture at 38kHz.
  server.send(200, "text/plain", "ok");
  Serial.println("send command ON");
  delay(2000);
}

void handleOFF() {
  irsend.sendRaw(rawDataOFF, 279, 38);  // Send a raw data capture at 38kHz.
  server.send(200, "text/plain", "ok");
  Serial.println("send command OFF");
  delay(2000);
}

void handleNotFound() {
  String message = "File Not Found\n\n";
  message += "URI: ";
  message += server.uri();
  message += "\nMethod: ";
  message += (server.method() == HTTP_GET) ? "GET" : "POST";
  message += "\nArguments: ";
  message += server.args();
  message += "\n";
  for (uint8_t i = 0; i < server.args(); i++) {
    message += " " + server.argName(i) + ": " + server.arg(i) + "\n";
  }
  server.send(404, "text/plain", message);
}
